					$(this).siblings().find('a').removeClass('active');
					$(this).find('a').addClass('active');
					$('.product-part').hide();
					$('.product-part').eq(index).show();

				});

				//加减号
				var num_input = $('.buy_num');
				var buy_size = $('.buy_size.active').text();
				function check_buy_num(type) {
					var buy_num = parseInt(num_input.val());
				    var v_max = $("#cur_sub_num").val();
				    var kucun = $("#cur_sub_num").val();
				    if (kucun <=0 ){
				    	$('.btn-plus').attr('disabled', true);
				    	$('.btn-minus').attr('disabled', true);
				    	$('.product-btn-hint').show().siblings().hide();
				    	return false;
				    };

					switch(type) {
						case '+':
							++buy_num;
						break;
						case '-':
							--buy_num;
						break;
						default:
						break;
					}

					if (isNaN(buy_num)) {
						buy_num = 1;
					};
					if (buy_num > v_max) {
						buy_num = v_max		
					};

					if (buy_num <= 0) {
						buy_num = 1;		
					};	
					num_input.val(buy_num);
				        //$$("#choosed_num").html('x '+buy_num);
				}

				$(document).on('click', '.btn-plus', function(e) {
					check_buy_num('+');
				});

				$('.buy_num input[name="number"]').on('blur', function(e) {
					check_buy_num();
				});


				$(document).on('click', '.btn-minus', function(e) {
					check_buy_num('-');
				});

				function reset_buy_num() {
					var _this = $('.buy_size.active');
					_this.addClass('active');
			        $("#cur_sub_id").val(_this.attr("data-id"));
			        $("#cur_sub_num").val(_this.attr("data-val"));	
				    $('.buy_num').val(1);
				    //处理没有库存的情况
				    
			    	var kucun = $("#cur_sub_num").val();
			    	if (kucun <=0 ){
			    		$('.btn-plus').attr('disabled', true);
			    		$('.btn-minus').attr('disabled', true);
			    		$('.product-btn-hint').show().siblings().hide();
			    	};
				   
				};

				reset_buy_num();

				$(document).on('click', '.buy_size', function(e) {
					var _this = $(this);
					$(this).siblings().removeClass('active');
					_this.addClass('active');
			        reset_buy_num();
				});
                                
